  createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
  }

  setTableLength() {
    if (this.state.tableExpanded) {
      this.setState({
        tableExpanded: false
      });
    } else {
      this.setState({
        tableExpanded: true
      });
    }
  }
